INSERT INTO `linea_translations` (`id`, `linea_id`, `name`, `short_name`, `description`, `locale`) VALUES
(1, 1, 'FILLER COLOR', NULL, 'Revolución del color formulada con su exclusiva tecnología HYALURON COMPLEX, brinda juventud, vida y COBERTURA ABOSLUTA de color a cada hebra capilar.\nCabello mas sano, fuerte, brilloso, hidratado, rejuvenecido y con un color vibrante por mucho mas tiempo.', 'es'),
(2, 1, 'FILLER COLOR', NULL, 'Color revolution formulated with its exclusive HYALURON COMPLEX technology, provides youth, life and ABSOLUTE COVERAGE of color to each hair strand.\nHealthier, stronger, shiny, hydrated, rejuvenated hair with a vibrant color for much longer.', 'en'),
(3, 2, 'COLOR COMPLEMENTS', NULL, 'xxx', 'es'),
(4, 2, 'COLOR COMPLEMENTS', NULL, 'xxx', 'en'),
(5, 3, 'PRO LUMINOUS', NULL, 'Sistema innovador desarrollado con tecnología LUMINOUS SYSTEM, que tiene como objetivo principal devolver BRILLO RADIANTE y VITALIDAD al cabello opaco y sin vida.', 'es'),
(6, 3, 'PRO LUMINOUS', NULL, 'Innovative system developed with LUMINOUS SYSTEM technology, whose main objective is to restore RADIANT SHINE and VITALITY to dull and lifeless hair.', 'en'),
(7, 4, 'PRO NUTRITIVE', NULL, 'Sistema innovador desarrollado con tecnología HYDRO ADVANCE SYSTEM, que tiene como objetivo principal devolver HIDRATACIÓN Y NUTRICIÓN al cabello reseco y deshidratado.', 'es'),
(8, 4, 'PRO NUTRITIVE', NULL, 'Innovative system developed with HYDRO ADVANCE SYSTEM technology, whose main objective is to restore HYDRATION AND NUTRITION to dry and dehydrated hair.', 'en'),
(9, 5, 'PRO REPAIR', NULL, 'Sistema innovador desarrollado con tecnología  REPARAGE SYSTEM , que tiene como objetivo principal REPARAR Y RESTAURAR la estructura capilar del cabello dañado.', 'es'),
(10, 5, 'PRO REPAIR', NULL, 'Innovative system developed with REPARAGE SYSTEM technology, whose main objective is to REPAIR AND RESTORE the capillary structure of damaged hair.', 'en'),
(11, 6, 'PRO FILLER', NULL, 'Desarrollada con tecnología HYALURON COMPLEX, basada en ÁCIDO HIALURÓNICO, que produce un efecto densificador y reparador inmediato, capaz de rellenar la fibra capilar desde el interior, fortaleciendo su estructura y sellando las cutículas.', 'es'),
(12, 6, 'PRO FILLER', NULL, 'Developed with HYALURON COMPLEX technology, based on HYALURONIC ACID, which produces an immediate densifying and repairing effect, capable of filling the hair fiber from the inside, strengthening its structure and sealing the cuticles.', 'en'),
(13, 7, 'POST LISS', NULL, 'INNOVADORA tecnología que mezcla los aminoácidos y los fragmenta en pequeñas moléculas, direccionándolas específicamente en cada parte del cabello DAÑADO de acuerdo a su necesidad.\n\nSofisticada gama de productos para cabellos ALISADOS, formulada con la tecnología HYDROACID COMPLEX, un sistema de 4 pasos que incluye la concentración más alta de aminoácidos.', 'es'),
(14, 7, 'POST LISS', NULL, 'INNOVATIVE technology that mixes amino acids and fragments them into small molecules, directing them specifically to each part of the DAMAGED hair according to its needs.\n\nSophisticated range of products for SMOOTHED hair, formulated with HYDROACID COMPLEX technology, a 4-step system that includes the highest concentration of amino acids.', 'en'),
(15, 8, 'PRO BALANCE', NULL, 'Exclusiva formula con pigmentos puros azules, con gran poder de matización y neutralización de los cabellos platinados.', 'es'),
(16, 8, 'PRO BALANCE', NULL, 'Exclusive formula with pure blue pigments, with great power of nuance and neutralization of platinum hair.', 'en'),
(17, 9, 'PRO CLEAN & SOFT', NULL, 'Formula especializada y exclusiva para uso en salones de belleza. Prepara y equilibra la hebra capilar ante los procesos químicos pre y post.', 'es'),
(18, 9, 'PRO CLEAN & SOFT', NULL, 'Specialized and exclusive formula for use in beauty salons. Prepares and balances the hair strand before the pre and post chemical processes.', 'en'),
(19, 10, 'FINISHING & FIXING', NULL, 'Formulas de alto desempeño para un optimo acabado del peinado, dando como resultado un cabello con flexibilidad, docilidad, durabilidad, sellado de puntas y protección de rayos solares y agentes externos.', 'es'),
(20, 10, 'FINISHING & FIXING', NULL, 'High-performance formulas for an optimal hairstyle finish, resulting in hair with flexibility, docility, durability, sealing of ends and protection from sunlight and external agents.', 'en'),
(21, 11, 'PRO RESIST', NULL, 'Formulas con gran poder de reparación y regeneración ante los daños extremos, causado por los procesos mecánicos, químicos y/o  ambientales.', 'es'),
(22, 11, 'PRO RESIST', NULL, 'Formulas with great repair and regeneration power against extreme damage caused by mechanical, chemical and/or environmental processes.', 'en'),
(23, 12, 'SALON TREATMENT', NULL, 'Líneas de tratamiento de alto impacto formuladas con Nanotecnología, que permite a los ingredientes naturales de la formulación actúen en lo más profundo de la hebra capilar, logrando así un efecto más potente en cuanto a la regeneración, reparación, nutrición e hidratación de la hebra capilar dañada.', 'es'),
(24, 12, 'SALON TREATMENT', NULL, 'High-impact treatment lines formulated with Nanotechnology, which allows the natural ingredients of the formulation to act in the depths of the hair strand, thus achieving a more powerful effect in terms of regeneration, repair, nutrition and hydration of the hair strand. damaged.', 'en');
